export default function olovaPlugin(options = {}) {
  return {
    name: "vite-plugin-olova",

    config() {
      return {
        esbuild: {
          jsxFactory: "h",
          jsxFragment: "Fragment",
          jsxInject: `import { h, Fragment } from 'olova'`,
        },
        resolve: {
          alias: {
            // Add any aliases you need
            "@olova": "olova",
          },
        },
        optimizeDeps: {
          include: ["olova"],
        },
      };
    },
  };
}
